.footer__store {
 color: #a5003c;
 font-weight: bold;
}

.menu__list {
 padding-top: 20px;
}
.logo_menu_lateral {
 height: 60px;
 padding-top: 10px;
 padding-left: 45px;
}
.banner-slider {
 max-width: 85rem;
 max-height: calc(90rem*var(--banner-aspect-ratio));
}
.text_menu_lateral {
 padding-left: 20px
}
.btn-primary {
 color: #fff;
 background-color: #66bb6a;
 border-color: #66bb6a;
}

/* espaço entre botão comprar verde e produto */
.btn.btn-primary.btn-block {
 background: #66bb6a;
 border: #66bb6a;
 margin-bottom: 32px;
}
.product-card .prices__discount, .product-card .product-card__prices, .product-card h3, .products-carousel .product-card__prices, .products-carousel h3, .products-carousel strong {
 text-align: center;
}
.prices__value {
 color: #a5003c;
}
.header__nav {
 padding-top: 23px;
}
.product-card__name {
 font-size: 15px;
}
.header__search {
 border-color: pink;
 border-style: double;
}
.btn.btn-lg.btn-primary {
 background: #28a745;
}
.banner-slider__slides, .banner-slider__slides > li {
	min-height: 0rem;
}
.banner-slider__slides, .banner-slider__slides > li {
	min-height: 0rem; 
}
.mb-lg-5, .my-lg-5 {
 margin-bottom: 0rem;
 margin-top: 0rem;
}
.stamps > li {
 max-width: 450px;
}
.badge-secondary, a.badge-secondary:focus, a.badge-secondary:hover {
 background-color: #a5003c;
}
.checkout__info {
 border-left: .3rem solid #a5003c !important;
}
.summary__amount__row--total strong {
 color: #a5003c !important;
}
.prices--literal .prices__discount span, .prices--literal .prices__installments span {
 color: #a5003c;
}



/* Tablet and desktop */
@media (min-width: 767px) {
 .header {
 top: 34px;
 }
 .top-bar {
 position: sticky;
 top: 0;
 z-index: 900;
 }
}
/* Mobile */
@media (max-width: 480px) {
 .header {
 top: 26px;
 }
 .top-bar {
 position: sticky;
 top: 0;
 z-index: 900;
 }
}


.prices__discount span {
 font-weight: bold !important;
 font-size: 150% !important;
 color: #a5003c; 
}


#lancamentos .container {
 display: flex !important;
 flex-direction: column;
 justify-content: center !important;
 align-items: center !important;
 text-align: center;
 font-size: 200%;
 color: #d20039;
 font-family: Clarika Office Grotesque DEMO Bold, sans-serif !important;
}
#lancamentos .container img {
 padding-bottom: 50px;
 width: auto;
 max-width: 450px;
}

.__pages_lancamentos .header,
.__pages_lancamentos .footer {
 display: none !important;
}

@media (max-width: 480px) {
 #lancamentos .container img {
 padding-bottom: 50px;
 max-width: 100% !important;
 }
}

}
.guia-tamanhos{
 padding: .2rem 1rem;
 background: #f1dde3;
 color: #fff;
 text-transform: uppercase;
 margin-left: 2rem;
 border-radius: 5px;
}
#modal-size-guide img{
max-width: 100%;
}


#account-form-birth,
label[for="account-form-birth"] {
display: none
}

.prices__value {
 color: #000;
}


.order-info__toggle {
 display: none !important;
}

/* ocultar compartilhamento */
#product-actions .btn.share {
display: none
}


/* esconder campo de cupom de desconto */
.discount-applier.cart__discount { display: none !important }


/* para deixar pix em primeiro ordem de pagamento, antes de boleto */
.checkout__col .payment .list-group {
display: flex;
}
.payment__gateway–1251[data-label="Pix"] {
order: -1;
border-top: 1px solid
}

/* para aparecer botão de compra na home*/
.product-card__buy.fade {
 opacity: 1 !important;
}

/* para esconder a frase descrição do produto */
p.lead {
 display: none;
}

/* para esconder valor riscado promocional */
span.prices__compare {
 display: none;
}

/* para esconder o valor promocional em verde que fica na imagem */
span.product__discount {
    display: none;
}

/* espaçamento entre os grids na pagina extra - referencias e depoimentos */
 .page--pages\/referencias-e-depoimentos .banners-grid {
 padding-bottom: 1.2rem;
 padding-top: 2rem;
 }
@media (min-width: 767px) {
 .page--pages\/referencias-e-depoimentos .banners-grid {
 padding-bottom: 2rem;
 }
}

/* tamanho da fonte do titulo do carrossel no desktop */
.products-carousel__title {
font-size: 26px !important;
}

/* cor e tamanho da fonte do nome do produto - desktop */
main .product-card__name {
color: #333;
font-size: 15px;
}

/* deixar o banner grid em scrool no celular - categorias */
@media (max-width: 578.59px) {
.banners-grid .row {
display: flex;
overflow-x: scroll;
flex-wrap: nowrap
}
.banners-grid .col-12 {
max-width: 66.6% !important;
flex: 0 0 66.66% !important;
}
 /* tamanho da fonte do titulo do carrossel no mobile e deixar em negrito*/
.products-carousel__title {
font-size: 19px !important;
font-weight: bold;
}
  /* cor e tamanho da fonte do nome do produto - mobile */
.product-card__name {
color: #333;
font-size: 14px !important;
}
}
/* fonte do titulo dos carroseis */
.products-carousel__title {
 font-family: Poppins;
}
/* borda e cor no icone banner tarja */
.info-bar__item .rounded-icon {
  border: 2px solid #a5003c; 
  color: #a5003c; 
  margin-right: 6px
}
/* nome do produto - desktop */
.product-card__name {
margin-bottom: 15px;
margin-bottom: 9px;
font-weight: 350;
}
/* rodapé - informações e categorias, estava em rosa, deixei em branco */
.footer__title[aria-expanded] {
color: #fff;
}
/* rodapé - tamanho das informações de contato, numero do whatsapp e email */
main .footer__contacts a {
font-size: 14px;
}
/* icone do menu via mobile */
.header__toggler-icon {
color: #fff;
font-size: 1.7rem;
}
/* colocar borda e cor de fundo no "ver toda a categoria" - menu lateral mobile */
.menu__link {
border-top: 0;
color: #000;
background: #ecd4dc;
border-radius: 10px;
}
/* icone carrinho - cor do numero dos itens */
main .header__buttons .badge-primary {
background-color: #2d2f2a
}
/* botão de comprar pg inicial e categoria - deixar arredondado, mudar cor e dar espaço entre botao e preço */
.btn.btn-primary.btn-block {
border-radius: 6px;
background: #28a745;
border: #28a745;
margin-top: 10px;
}
/* botão de comprar pg do produto - deixar largo e arredondado */
main .product__buy .btn-lg {
width: 100%;
border-radius: 7px;
}
/* para exibir a frase "sem juros"  */
.product-card__prices .prices__installments:after {
  content: ' sem juros'
}
